/*
*
* TooLong (Minified version)
* jQuery plugin to shorten elements text
* http://code.google.com/p/toolong/
* Version 1.0
*
* By Guillaume Gaubert
* http://widgetulous.com/
*
* Date: November, 23 of 2010 11:58AM
*
* Basic use :
* $('#element').TooLong({ 'len' : 14 });
*
*/

(function($){$.fn.TooLong=function(i){var j={'len':20,'cutter':'...','count':false,'position':'right'};return this.each(function(){if(i){$.extend(j,i)}var a=$(this).text().length;var b=0;if(j.count){var b=j.cutter.length}if(a>j.len){var c=$(this).text();if(j.position=="right"){c=c.substr(0,(a-(a-j.len))-b)+j.cutter}if(j.position=="left"){c=j.cutter+c.substr((a-j.len)+b,(a-(a-j.len)))}if(j.position=="middle"){var d=Math.floor(j.len/2);var e=Math.floor(j.cutter.length/2);var f=d-e;var g=c.substr(0,f);var h=c.substr(-d);c=g+j.cutter+h}$(this).text(c)}})}})(jQuery);